						function clickLink(sender) {
							var url = sender.prop('href');
							loadUrlToFrame(url);
						};

						function loadFrame(sender) {
							var siteIndex = sender.prop("siteindex");
							loadUrlToFrame(sites[siteIndex].url)
						};

						function loadUrlToFrame(url) {
							// Load content to frame
							$("#display_frame").attr('src', url);
							setCarouselNav(url);
						}

						function setCarouselNav(url) {
							// Set live preview link
							$("a#linkLivePreview").prop('href', url);

							// Get position	
							var curIndex = sites.map(function(e) {
								return e.url;
							}).indexOf(url);

							// Previous
							var prevIndex = curIndex - 1;
							if (prevIndex < 0) {
								prevIndex = sites.length - 1;
							}

							// Next
							var nextIndex = curIndex + 1;
							if (nextIndex > (sites.length - 1)) {
								nextIndex = 0;
							}

							// Preload frame content;
							// load previous 
							preloadContent(sites[prevIndex].url, prevIndex);
							$("#modal-webitPreview #btnPrevious").prop('siteindex', prevIndex);
							// load next
							preloadContent(sites[nextIndex].url, nextIndex);
							$("#modal-webitPreview #btnNext").prop('siteindex', nextIndex);
						}
					</script>
